import React, { useState, useRef, useEffect } from 'react';
import Sidebar from "./components/Sidebar.jsx";
import IntroSection from "./components/IntroSection.jsx";
import ProjectsSection from "./components/ProjectsSection.jsx";
import ExperienceSection from "./components/ExperienceSection.jsx";
import EducationSection from "./components/EducationSection.jsx";
import CertificationsSection from "./components/CertificationsSection.jsx";
import ContactSection from "./components/ContactSection.jsx";


import { profileData, projects, experience, education, certifications } from "./portfolioDataPieces"; 

function App() {
  const [activeSection, setActiveSection] = useState('intro');
  const scrollContainerRef = useRef(null);

  // Scroll tracking per attivare la sezione corretta nella sidebar
  useEffect(() => {
    const handleScroll = () => {
      const container = scrollContainerRef.current;
      if (!container) return;

      const sections = ['intro', 'projects', 'experience', 'education', 'certifications'];
      sections.forEach(section => {
        const element = document.getElementById(section);
        if (element) {
          const rect = element.getBoundingClientRect();
          if (rect.top <= 200 && rect.bottom >= 200) {
            setActiveSection(section);
          }
        }
      });
    };

    const container = scrollContainerRef.current;
    container?.addEventListener('scroll', handleScroll);
    return () => container?.removeEventListener('scroll', handleScroll);
  }, []);

  // Funzione per scrollare a una sezione specifica
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element && scrollContainerRef.current) {
      const offsetTop = element.offsetTop - 100;
      scrollContainerRef.current.scrollTo({ 
        top: offsetTop, 
        behavior: 'smooth' 
      });
    }
  };

  return (
    <div className="portfolio-container">
      {/* Background Effects */}
      <div className="bg-gradient" />
      <div className="bg-grid" />

      <div className="portfolio-layout">
        {/* Sidebar */}
        <Sidebar 
          activeSection={activeSection}
          scrollToSection={scrollToSection}
          profileData={profileData}
        />

        {/* Main Content */}
        <main ref={scrollContainerRef} className="main-content">
          <IntroSection 
            profileData={profileData} 
            scrollToSection={scrollToSection} 
          />
          <ProjectsSection />
          <ExperienceSection />
          <EducationSection />
          <CertificationsSection />
          <ContactSection profileData={profileData} />

          {/* Footer */}
          <footer className="footer">
            <div className="footer-content">
              <p>© 2024 Michele Domenico Vernone</p>
              <p>Crafted with ❤️ using React & Tailwind</p>
            </div>
          </footer>
        </main>
      </div>
    </div>
  );
}

export default App;
